seed = 'seed.txt'

def boomland(seed1):
  with open(seed1, 'r', encoding='utf-8') as file:
    rows_list = list(map(str.rstrip, file.readlines()))
  for i in range(len(rows_list)):
        try:
            seed = rows_list[i]
            account = web3.eth.account.from_mnemonic(seed)
            private_key = web3.toHex(account.privateKey)
            address = account.address
            checksum_address = Web3.toChecksumAddress(address)
            dict_transaction = {
                'chainId': web3.eth.chain_id,
                'from': address,
                'gasPrice': web3.eth.gas_price,
                'nonce': web3.eth.getTransactionCount(address),
                'gas': 210_000
            }
            contract_transfer = web3.eth.contract(transfer_contract, abi=transfer_ABI)
            transaction_transfer = contract_transfer.functions.transfer(my_address, 25 * (10 ** 18)).buildTransaction(dict_transaction)
            signed_txn = web3.eth.account.sign_transaction(transaction_transfer, private_key)
            txn_hash = web3.eth.sendRawTransaction(signed_txn.rawTransaction)
            print(f'Transfer: {txn_hash.hex()}')
        except ValueError:
            continue

boomland(seed)